import React, { Component } from 'react';
import './Inputs.css';
import { Container, Row, Col } from 'react-bootstrap';

class Inputs extends Component {
    constructor(props) {
        super(props);
        this.state = {
            value: 'Input the events here',
            rows: 25,
            minRows: 25,
            maxRows: 100,
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        this.setState({ value: event.target.value });
    }

    handleSubmit(event) {
        alert('Chart has been plotted! ' + this.state.value);
        event.preventDefault();
    }

    render() {
        return (
            <textarea name="text" id="inputBox" value={this.state.value} onChange={this.handleChange} ref={ref => this.myTextInput = ref}>

            </textarea>
