import React, { Component } from 'react';
import './Inputs.css';
import { Container, Row, Col } from 'react-bootstrap';

class Inputs extends Component {
    constructor(props) {
        super(props);
        this.state = {
            value: 'Input the events here',
            rows: 25,
            minRows: 25,
            maxRows: 100,
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange = (event) => {
        const textareaLineHeight = 24;
        const { minRows, maxRows } = this.state;

        const previousRows = event.target.rows;
        event.target.rows = minRows; // reset number of rows in textarea 

        const currentRows = ~~(event.target.scrollHeight / textareaLineHeight);

        if (currentRows === previousRows) {
            event.target.rows = currentRows;
        }

        if (currentRows >= maxRows) {
            event.target.rows = maxRows;
            event.target.scrollTop = event.target.scrollHeight;
        }

        this.setState({
            value: event.target.value,
            rows: currentRows < maxRows ? currentRows : maxRows,
        });
    };

    handleSubmit(event) {
        alert('Chart has been plotted! ' + this.state.value);
        event.preventDefault();
    }

    render() {
        return (
            <textarea name="text" id="inputBox" value={this.state.value} onChange={this.handleChange} ref={ref => this.myTextInput = ref}>

            </textarea>
            // <Container fluid={true} id="input-wrapper">
            //     <Row noGutters={true} >
            //         <Col>

            //         </Col>
            //     </Row>
            // </Container >

        );
    }
}

export default Inputs;
