import React, { Component } from 'react';
import './Inputs.css';
import { Container, Row, Col } from 'react-bootstrap';

class Inputs extends Component {
    constructor(props) {
        super(props);
        this.state = {
            value: 'Input the events here'
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        this.setState({ value: event.target.value });
    }

    handleSubmit(event) {
        alert('Chart has been plotted! ' + this.state.value);
        event.preventDefault();
    }

    render() {
        return (
            <Container id="input-wrapper">
                <Row noGutters={true} >
                    <Col>
                        <div id="lineNumbers">
                            1
                            2
                            3
                            4
                            </div>
                    </Col>
                    <Col>
                        <textarea name="text" id="inputBox" value={this.state.value} onChange={this.handleChange} ref={ref => this.myTextInput = ref}>

                        </textarea>
                    </Col>
                </Row>
            </Container >

        );
    }
}

export default Inputs;
